// PublicPage.js

import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Card } from "primereact/card";
import { Button } from "primereact/button";

import "./PublicPage.css"; // Importez un fichier CSS pour les styles personnalisés

import secretStoryLogo from "./images/Secret_Story_Logo.png"; // Importez l'image

const participantsData = [
  { place: 1, name: "Participant 1", cagnotte: 100 },
  { place: 2, name: "Participant 2", cagnotte: 150 },
  { place: 3, name: "Participant 3", cagnotte: 75 },
  // Ajoutez plus de données de participants ici
];

const PublicPage = () => {
  // Fonction de rendu personnalisée pour la colonne "Place"
  const renderPlace = (rowData) => {
    return <span>{rowData.place}</span>;
  };

  return (
    <div className="public-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        <DataTable value={participantsData} className="datatable">
          {/* Colonne pour le numéro de place */}
          <Column field="place" header="#" body={renderPlace} />

          <Column field="name" header="Nom du participant" />
          <Column field="cagnotte" header="Cagnotte" pi-refresh />
        </DataTable>
      </Card>
      <Button label="Actualiser le classement" className="refresh-button" />
    </div>
  );
};

export default PublicPage;
