import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { classNames } from "primereact/utils";
import { RankingProvider, useRanking } from "./RankingContext";
import "./PublicPage.css";
import secretStoryLogo from "./images/Secret_Story_Logo.png";
// Importez les images des participants
import Zonz from "./images/profile/Zonz.png";
// Importez les autres images des participants ici

const PublicPage = () => {
  const { rankings, loading } = useRanking();

  const renderPlace = (rowData) => {
    return <span className="circle-pink">{rowData.place}</span>;
  };

  // Fonction pour générer l'URL de l'image du participant en fonction de son nom
  const getParticipantImageUrl = (participantName) => {
    return `./images/profile/${participantName}.png`;
  };

  return (
    <div className="public-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <DataTable value={rankings} className="datatable" stripedRows>
            <Column
              header="#"
              headerStyle={{ width: "3rem" }}
              body={(data, options) => (
                <span className="circle-pink">{options.rowIndex + 1}</span>
              )}
            />
            <Column
              field="nom"
              header="Nom du participant"
              body={(rowData) => (
                <>
                  <img
                    src={getParticipantImageUrl(rowData.nom)}
                    alt={rowData.nom}
                    className="participant-image"
                  />
                  {rowData.nom}
                </>
              )}
            />
            <Column field="cagnotte" header="Cagnotte" />
          </DataTable>
        )}
      </Card>
      <Button
        label="Actualiser le classement"
        icon="pi pi-refresh"
        className={classNames("refresh-button")}
      />
    </div>
  );
};

const App = () => {
  return (
    <RankingProvider>
      <PublicPage />
    </RankingProvider>
  );
};

export default App;
