import React, { useEffect, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { classNames } from "primereact/utils";
import { RankingProvider, useRanking } from "./RankingContext";
import Menu from "./Menu"; // Importez le composant Menu
import "./PublicPage.css";
import secretStoryLogo from "./images/Secret_Story_Logo.png";
import axios from "axios"; // Importez Axios pour effectuer des requêtes HTTP

const AdminPage = () => {
  const { rankings, loading } = useRanking();
  const [nominants, setNominants] = useState([]); // Liste de nominants
  const [nominees, setNominees] = useState([]); // Liste de nominés
  const [secrets, setSecrets] = useState([]); // Liste de secrets
  const [selectedNominant, setSelectedNominant] = useState(""); // Nominant sélectionné
  const [selectedNominee, setSelectedNominee] = useState(""); // Nominé sélectionné
  const [selectedSecret, setSelectedSecret] = useState(""); // Secret sélectionné

  useEffect(() => {
    if (!loading) {
      // Récupérez la liste des nominants, des nominés et des secrets depuis l'API
      // Assurez-vous d'adapter ces appels à votre propre API
      axios.get("/api/nominants").then((response) => {
        setNominants(response.data);
      });

      axios.get("/api/nominees").then((response) => {
        setNominees(response.data);
      });

      axios.get("/api/secrets").then((response) => {
        setSecrets(response.data);
      });
    }
  }, [loading]);

  const handleNominate = () => {
    // Effectuez la requête POST vers l'API /nominate avec les données sélectionnées
    // Assurez-vous d'adapter cette requête à votre propre API
    axios
      .post("/api/nominate", {
        nominant: selectedNominant,
        nominee: selectedNominee,
        secret: selectedSecret,
      })
      .then((response) => {
        // Vous pouvez gérer la réponse ici, par exemple, afficher un message de confirmation.
        console.log("Nomination réussie !");
      })
      .catch((error) => {
        // Gérez les erreurs ici, par exemple, afficher un message d'erreur.
        console.error("Erreur lors de la nomination : ", error);
      });
  };

  return (
    <div className="public-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        <Menu />
        {loading ? (
          <div>Loading...</div>
        ) : (
          <div>
            <h1>Administration</h1>
            <div>
              <label>Sélectionnez le nominant :</label>
              <select
                value={selectedNominant}
                onChange={(e) => setSelectedNominant(e.target.value)}
              >
                <option value="">-- Sélectionnez un nominant --</option>
                {nominants.map((nominant) => (
                  <option key={nominant.id} value={nominant.id}>
                    {nominant.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label>Sélectionnez le nominé :</label>
              <select
                value={selectedNominee}
                onChange={(e) => setSelectedNominee(e.target.value)}
              >
                <option value="">-- Sélectionnez un nominé --</option>
                {nominees.map((nominee) => (
                  <option key={nominee.id} value={nominee.id}>
                    {nominee.name}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label>Sélectionnez le secret :</label>
              <select
                value={selectedSecret}
                onChange={(e) => setSelectedSecret(e.target.value)}
              >
                <option value="">-- Sélectionnez un secret --</option>
                {secrets.map((secret) => (
                  <option key={secret.id} value={secret.id}>
                    {secret.description}
                  </option>
                ))}
              </select>
            </div>
            <Button
              label="Nominate"
              icon="pi pi-check"
              className={classNames("nominate-button")}
              onClick={handleNominate}
            />
          </div>
        )}
      </Card>
      <Button
        label="Actualiser le classement"
        icon="pi pi-refresh"
        className={classNames("refresh-button")}
      />
    </div>
  );
};

const App = () => {
  return (
    <RankingProvider>
      <AdminPage />
    </RankingProvider>
  );
};

export default App;
