import React, { useState } from "react";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { SelectButton } from "primereact/selectbutton";
import { RankingProvider, useRanking } from "./RankingContext";
import Menu from "./Menu";
import "./AdminPage.css";
import secretStoryLogo from "./images/Secret_Story_Logo.png";
import axios from "axios";

const AdminPage = () => {
  const { rankings, loading } = useRanking();
  const [selectedNominant, setSelectedNominant] = useState(null);
  const [selectedNominee, setSelectedNominee] = useState(null);
  const [selectedSecret, setSelectedSecret] = useState(null);

  const handleNomination = async () => {
    if (selectedNominant && selectedNominee && selectedSecret) {
      try {
        const response = await axios.post("/api/nominate", {
          nominant: selectedNominant,
          nominee: selectedNominee,
          secret: selectedSecret,
        });

        console.log("Nomination successful:", response.data);
        // Mettez à jour le classement ou effectuez d'autres actions nécessaires
      } catch (error) {
        console.error("Error nominating:", error);
        // Gérez les erreurs de nomination
      }
    }
  };

  return (
    <div className="admin-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        <Menu />
        <div className="nomination-form">
          <h2>Formulaire de Nomination</h2>
          <div className="form-row">
            <label>Nominant:</label>
            <SelectButton
              optionLabel="name"
              optionValue="id"
              options={nominantOptions}
              value={selectedNominant}
              onChange={(e) => setSelectedNominant(e.value)}
            />
          </div>
          <div className="form-row">
            <label>Nominé:</label>
            <SelectButton
              optionLabel="name"
              optionValue="id"
              options={nomineeOptions}
              value={selectedNominee}
              onChange={(e) => setSelectedNominee(e.value)}
            />
          </div>
          <div className="form-row">
            <label>Secret:</label>
            <SelectButton
              optionLabel="name"
              optionValue="id"
              options={secretOptions}
              value={selectedSecret}
              onChange={(e) => setSelectedSecret(e.value)}
            />
          </div>
          <button className="p-button" onClick={handleNomination}>
            Nommer
          </button>
        </div>
      </Card>
      <Button
        label="Actualiser le classement"
        icon="pi pi-refresh"
        className="refresh-button"
      />
    </div>
  );
};

const App = () => {
  return (
    <RankingProvider>
      <AdminPage />
    </RankingProvider>
  );
};

export default App;
