import React from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { RankingProvider, useRanking } from "./RankingContext"; // Importez le contexte

import "./PublicPage.css";
import secretStoryLogo from "./images/Secret_Story_Logo.png";

const PublicPage = () => {
  const { rankings, loading } = useRanking(); // Utilisez le hook personnalisé

  const renderPlace = (rowData) => {
    return <span className="place-badge">{rowData.place}</span>;
  };

  const formatCagnotte = (rowData) => {
    return `${rowData.cagnotte} €`; // Ajoutez le symbole euro ici
  };

  return (
    <div className="public-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <DataTable value={rankings} className="datatable" stripedRows>
            <Column
              header="#"
              headerStyle={{ width: "3rem" }}
              body={renderPlace}
              bodyStyle={{
                textAlign: "center",
                borderRadius: "50%",
                backgroundColor: "#007ad9",
                color: "#fff",
                display: "inline-block",
                width: "24px",
                height: "24px",
                lineHeight: "24px",
              }}
            ></Column>
            <Column field="nom" header="Nom du participant" />
            <Column
              field="cagnotte"
              header="Cagnotte"
              body={formatCagnotte} // Utilisez la fonction formatCagnotte pour ajouter le symbole euro
            />
          </DataTable>
        )}
      </Card>
      <Button
        label="Actualiser le classement"
        icon="pi pi-refresh"
        className={classNames("refresh-button")}
      />
    </div>
  );
};

const App = () => {
  return (
    <RankingProvider>
      <PublicPage />
    </RankingProvider>
  );
};

export default App;
