import React, { useState, useEffect } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Card } from "primereact/card";
import { Button } from "primereact/button";
import { classNames } from "primereact/utils";
import secretStoryLogo from "./images/Secret_Story_Logo.png";

const PublicPage = () => {
  const [participantsData, setParticipantsData] = useState([]);

  useEffect(() => {
    // Fonction pour charger le classement depuis l'API
    const loadRanking = async () => {
      try {
        const response = await fetch("/api/rankings"); // L'URL de votre API
        if (response.ok) {
          const data = await response.json();
          setParticipantsData(data);
        } else {
          console.error("Erreur lors du chargement du classement.");
        }
      } catch (error) {
        console.error("Erreur lors du chargement du classement.", error);
      }
    };

    // Appelez la fonction pour charger le classement lors du chargement du composant
    loadRanking();
  }, []); // Le tableau vide [] signifie que cette fonction s'exécutera une seule fois après le rendu initial

  // Fonction de rendu personnalisée pour la colonne "Place"
  const renderPlace = (rowData) => {
    return <span>{rowData.place}</span>;
  };

  return (
    <div className="public-page">
      <img
        src={secretStoryLogo}
        alt="Logo Secret Story"
        className="logo"
        style={{ width: "300px", height: "auto" }}
      />
      <Card className="card">
        <DataTable value={participantsData} className="datatable" stripedRows>
          {/* Colonne pour le numéro de place */}
          <Column field="place" header="#" body={renderPlace} />

          <Column field="name" header="Nom du participant" />
          <Column field="cagnotte" header="Cagnotte" />
        </DataTable>
      </Card>
      <Button
        label="Actualiser le classement"
        icon="pi pi-refresh"
        className={classNames("refresh-button")}
      />
    </div>
  );
};

export default PublicPage;
